package main

import "fmt"

func main() {
	fmt.Println(minCut("aab"))
	fmt.Println(minCut(""))
	fmt.Println(minCut(""))
}

func minCut(s string) int {
	min := len(s) - 1
	var search func(int, int)
	seen := map[int]int{}
	search = func(i int, c int) {
		if v, ok := seen[i]; ok && v < c {
			return
		}
		seen[i] = c
		if c > min {
			return
		}
		if i == len(s) {
			if c <= min {
				min = c - 1
			}
			return
		}
		for l := len(s) - i; l > 0; l-- {
			valid := true
			for j := 0; j < l/2; j++ {
				valid = valid && s[i+j] == s[i+(l-j-1)]
			}
			if !valid {
				continue
			}
			search(i+l, c+1)
		}
	}
	search(0, 0)
	return min
}
